/* ==UserStyle==
@name         YouTube Netflix Subtitles
@namespace    USO Archive
@author       @ang3lo_azedo
@description  The famous Netflix subtitltes with the drop shadow for YouTube.
@version      20211221.15.46
@license      NONE
@preprocessor uso
==/UserStyle== */
@-moz-document url-prefix("https://www.youtube.com/watch") {
/*Fonts */
 @font-face {
	font-family:'Netflix Sans';
	font-weight:100;
	src:url(https://assets.nflxext.com/ffe/siteui/fonts/netflix-sans/v3/NetflixSans_W_Th.woff2) format('woff2'), url(https://assets.nflxext.com/ffe/siteui/fonts/netflix-sans/v3/NetflixSans_W_Th.woff) format('woff')
}
@font-face {
	font-family:'Netflix Sans';
	font-weight:300;
	src:url(https://assets.nflxext.com/ffe/siteui/fonts/netflix-sans/v3/NetflixSans_W_Lt.woff2) format('woff2'), url(https://assets.nflxext.com/ffe/siteui/fonts/netflix-sans/v3/NetflixSans_W_Lt.woff) format('woff')
}
@font-face {
	font-family:'Netflix Sans';
	font-weight:400;
	src:url(https://assets.nflxext.com/ffe/siteui/fonts/netflix-sans/v3/NetflixSans_W_Rg.woff2) format('woff2'), url(https://assets.nflxext.com/ffe/siteui/fonts/netflix-sans/v3/NetflixSans_W_Rg.woff) format('woff')
}
@font-face {
	font-family:'Netflix Sans';
	font-weight:700;
	src:url(https://assets.nflxext.com/ffe/siteui/fonts/netflix-sans/v3/NetflixSans_W_Md.woff2) format('woff2'), url(https://assets.nflxext.com/ffe/siteui/fonts/netflix-sans/v3/NetflixSans_W_Md.woff) format('woff')
}
@font-face {
	font-family:'Netflix Sans';
	font-weight:800;
	src:url(https://assets.nflxext.com/ffe/siteui/fonts/netflix-sans/v3/NetflixSans_W_Bd.woff2) format('woff2'), url(https://assets.nflxext.com/ffe/siteui/fonts/netflix-sans/v3/NetflixSans_W_Bd.woff) format('woff')
}
@font-face {
	font-family:'Netflix Sans';
	font-weight:900;
	src:url(https://assets.nflxext.com/ffe/siteui/fonts/netflix-sans/v3/NetflixSans_W_Blk.woff2) format('woff2'), url(https://assets.nflxext.com/ffe/siteui/fonts/netflix-sans/v3/NetflixSans_W_Blk.woff) format('woff')
}
.ytp-caption-segment {
	--lt-color-gray-100: #f8f9fc;
	--lt-color-gray-200: #f1f3f9;
	--lt-color-gray-300: #dee3ed;
	--lt-color-gray-400: #c2c9d6;
	--lt-color-gray-500: #8f96a3;
	--lt-color-gray-600: #5e636e;
	--lt-color-gray-700: #2f3237;
	--lt-color-gray-800: #1d1e20;
	--lt-color-gray-900: #111213;
	--lt-shadowDefault: 0 2px 6px -1px rgba(0, 0, 0, 0.16), 0 1px 4px -1px rgba(0, 0, 0, 0.04);
	--lt-shadowActive: 0 0 8px -2px rgba(0, 0, 0, 0.1), 0 6px 20px -3px rgba(0, 0, 0, 0.2);
	--lt-color-white: #fff !important;
	--lt-color-black: #111213 !important;
	--lt-color-transparent: rgba(255, 255, 255, 0) !important;
	--lt-color-background-light: var(--lt-color-gray-100) !important;
	--lt-color-background-default: var(--lt-color-gray-200) !important;
	--lt-color-background-dark: var(--lt-color-gray-300) !important;
	--lt-color-border-light: var(--lt-color-gray-200) !important;
	--lt-color-border-default: var(--lt-color-gray-300) !important;
	--lt-color-border-dark: var(--lt-color-gray-400) !important;
	--lt-color-text-very-light: var(--lt-color-gray-500) !important;
	--lt-color-text-light: var(--lt-color-gray-600) !important;
	--lt-color-text-default: var(--lt-color-gray-700) !important;
	--lt-color-text-dark: var(--lt-color-gray-800) !important;
	--lt-color-overlay-default: #fff !important;
	--lt-color-overlay-dark: #fff !important;
	--lt-color-overlay-transparent: rgba(0, 0, 0, 0.1) !important;
	--lt-shadow-website-overlay: 0 0 7px 0 rgba(0, 0, 0, 0.3) !important;
	text-size-adjust: none !important;
	-webkit-font-smoothing: antialiased !important;
	user-select: none !important;
	direction: ltr !important;
	white-space: nowrap !important;
	text-align: center !important;
	line-height: normal !important;
	color: #ffffff !important;
	text-shadow: #000000 0px 0px 7px !important;
	font-family: Netflix Sans, Helvetica Nueue, Helvetica, Arial, sans-serif !important;
	font-weight: bolder !important;
}
.ytp-big-mode > div > div > span > span > span {
	font-size: 34.5px !important;
}
.ytp-hide-info-bar > div > div > span > span > span {
	font-size: 27.5px !important;
}
.ytp-caption-segment {
	background: none !important;
}
.caption-window {
	text-align: center !important;
	width: 100% !important;
	height: auto !important;
	left: 0% !important;
	margin-left: auto !important;
}
.ytp-big-mode > div > .caption-window, .ytp-hide-info-bar > div > .caption-window {
	bottom: 6.9468% !important;
}
.ytp-big-mode.ytp-autohide-active > div > .caption-window, .ytp-autohide > div > .caption-window {
	bottom: 10% !important;
}
}

/* ==UserStyle==
@name         osu!cc
@namespace    USO Archive
@author       cyperdark
@description  OUTDATED, NEW ONE https://userstyles.world/style/1767/osu-color-changer
@version      20211030.01.52
@license      NONE
@preprocessor uso
@advanced dropdown color "Choose a color" {
    red "Red*" <<<EOT 0 EOT;
    orange "Orange" <<<EOT 30 EOT;
    yellow "Yellow" <<<EOT 60 EOT;
    lime "Lime" <<<EOT 85 EOT;
    green "Green" <<<EOT 120 EOT;
    Cyan "Cyan" <<<EOT 180 EOT;
    blue "Blue" <<<EOT 240 EOT;
    Grey-purple "Grey-purple" <<<EOT 255 EOT;
    magenta "Magenta" <<<EOT 300 EOT;
    pink "Pink" <<<EOT 330 EOT;

}
@advanced dropdown bg "Custom background" {
    test "Default*" <<<EOT https://w.wallhaven.cc/full/md/wallhaven-md3vjm.jpg EOT;

	bg-custom-dropdown "Custom" <<<EOT /*[[bg-custom]]*\/ EOT;
}
@advanced text bg-custom "Custom background (Custom)" "https://example.com/image.png"
@advanced text bgpos "Background position" "center"
@advanced text bgsize "Background size" "cover"
@advanced dropdown bgrepeat "background repeat" {
    no-repeat "No repeat*" <<<EOT no-repeat EOT;
    repeat "Repeat" <<<EOT repeat EOT;
    repeat-x "Repeat horizontally" <<<EOT repeat-x EOT;
    repeat-y "Repeat vertically" <<<EOT repeat-y EOT;
    round "Round" <<<EOT round EOT;
    space "Space" <<<EOT space EOT;

}
@advanced dropdown bgblend "Background blend mode" {
    normal "normal*" <<<EOT normal EOT;
    multiply "multiply" <<<EOT multiply EOT;
    screen "screen" <<<EOT screen EOT;
    overlay "overlay" <<<EOT overlay EOT;
    darken "darken" <<<EOT darken EOT;
    lighten "lighten" <<<EOT lighten EOT;
    color-dodge "color-dodge" <<<EOT color-dodge EOT;
    color-burn "color-burn" <<<EOT color-burn EOT;
    hard-light "hard-light" <<<EOT hard-light EOT;
    soft-light "soft-light" <<<EOT soft-light EOT;
    difference "difference" <<<EOT difference EOT;
    exclusion "exclusion" <<<EOT exclusion EOT;
    hue "hue" <<<EOT hue EOT;
    saturation "saturation" <<<EOT saturation EOT;
    color "color" <<<EOT color EOT;
    luminosity "luminosity" <<<EOT luminosity EOT;

}
==/UserStyle== */
@-moz-document url-prefix("https://osu.ppy.sh/") {
:root {
    --base-hue: /*[[color]]*/;
    --base-hue-deg: /*[[color]]*/deg;
}
body, html {scrollbar-color: hsl(var(--base-hue),100%,70%) #000;}
/*.user-action-button--mutual {background: hsl(var(--base-hue),40%,60%);}
.user-action-button--mutual:hover {background: hsl(var(--base-hue),30%,50%);}*/
.header-v4__bg-container {z-index: -1;}
::-webkit-scrollbar {width: 10px;}
::-webkit-scrollbar-thumb {
    background-color: hsl(var(--base-hue),100%,70%);
    border-radius: 100px;
}
