@-moz-document domain('web.whatsapp.com') {
    :root:not(#z), .dark:not(#z) {
        /// Userstyle info.
        --version: 'ðŸŒš Dark-WhatsApp v3.7.2 â€” March 31st, 2023'

        /// UI font.
        --ui-font: ui_font, Segoe UI, Helvetica Neue, Helvetica, Lucida Grande,
