        /// Generate DWA color variables.
        for _v in (bg fg ac b1 b2 b3 b4 b5 b6 b7 b8) {
            for _i in 0..5 {
                key = s('--%s-%s', _v, _i)
                /// Escape `\` for `*-0` vars.
                val = _v + \- + _i

                /// Generate CSS variables.
                { key }: val
                { key + '-rgb' }: to_rgb(val)
            }
        }

        /// Misc colors.
        --white:      _white
        --white-rgb:  to_rgb(_white)
        --sh:         _sh
        --sh-rgb:     to_rgb(_sh, 1)
        --t:          transparent
        --bshadow:    0 4px 8px 2px var(--shadow)

        /// Theme intro colors.
        svg_bg = to_svg(bg-2)
        svg_ac = to_svg(ac-0)

