        --intro-image: _i
    }

    // Animation for 'Typing...' indicator.
    if (compact_mode) {
        @keyframes aBorder {
            40% { opacity: 0 }
            80% { opacity: 1 }
        }
    }

    // Feat -> Animate emojis.
    if (emoji_a) {
        @keyframes aPulse {
          0%   { transform: scale(.9) }
          50%  { transform: scale(1)  }
          100% { transform: scale(.9) }
        }

        @keyframes aKiss {
          0%   { transform: scale(.7) }
          50%  { transform: scale(.8) }
          80%  { transform: scale(1) rotate(-30deg) }
          100% { transform: scale(.7) }
        }
    }

    // Global styles.
    html > body {
        background-image: none i
        c: fg0 0 bg0

        option { c: fg1 0 bg3 }

        /// Feat -> Custom font.
        &, button, input { font-family: var(--ui-font) }

        /// Toast notifications.
        ._1Ftww {
            c: fg1
            border: 1px solid bg5 i
            button:nth-child(2) { c: ac1 }
            button:nth-child(3) { c: fg4 }
        }

        /// Intro -> Image.
        [data-testid^="intro-md-beta-logo-"] {
            padding: 8rem
            background-image: var(--intro-image)
            > svg { display: none }
        }

        /// Intro -> Version.
        [data-testid="intro-title"] {
            font-size: 0px i
            &::before {
                content: var(--version)
                font-size: 1.5rem
            }
        }

        /// Intro -> Announcements.
        [data-testid="intro-text"] {
            font-size: 0px i
            &::before {
                content: msg
                white-space: pre-wrap
                font-size: 0.875rem
            }
        }

        /// Settings -> Tweaks for 'Set Chat Wallpaper' area. #149
